function rightTrim(e){var r=/^([\w\W]*)(\b\s*)$/;return r.test(e)&&(e=e.replace(r,"$1")),e}function leftTrim(e){var r=/^(\s*)(\b[\w\W]*)$/;return r.test(e)&&(e=e.replace(r,"$2")),e}function trimAll(e){var r=/^(\s*)$/;return r.test(e)&&(e=e.replace(r,""),0==e.length)?e:(r=/^(\s*)([\W\w]*)(\b\s*$)/,r.test(e)&&(e=e.replace(r,"$2")),e)}function removeCommas(e){var r=/,/g;return e.replace(r,"")}function addCommas(e){for(var r=new RegExp("(-?[0-9]+)([0-9]{3})");r.test(e);)e=e.replace(r,"$1,$2");return e}function addDecimalPoints(e){for(var r=new RegExp("(-?[0-9]+)([0-9]{3})");r.test(e);)e=e.replace(r,"$1.$2");return e}function removeCharacters(e,r){var t=new RegExp(r,"gi");return e.replace(t,"")}